import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './Home.css'; // Import gradient styles

interface LocationState {
  productType: string;
  prompt: string;
}

const Generator: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const state = location.state as LocationState;

  const [refinedPrompt, setRefinedPrompt] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(true);
  const [selectedImage, setSelectedImage] = useState<string>('');

  // Mock images for demonstration
  const mockImages = [
    '/mock-images/design1.jpg',
    '/mock-images/design2.jpg',
    '/mock-images/design3.jpg'
  ];

  useEffect(() => {
    if (!state?.prompt) {
      navigate('/');
      return;
    }

    const simulatePromptRefinement = async () => {
      setLoading(true);
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1500));

      // Mock refined prompt
      setRefinedPrompt(`${state.prompt} - 現代風格 - 鋁合金材質 - RGB燈效`);
      setLoading(false);
    };

    simulatePromptRefinement();
  }, [state, navigate]);

  const handleImageSelect = (imageUrl: string) => {
    setSelectedImage(imageUrl);
  };

  const handleSaveToKnowledgeBase = () => {
    // Mock saving to knowledge base
    alert('設計已加入知識庫！');
  };

  const handleBackToPrompt = () => {
    // Navigate back to the appropriate product page based on the product type
    if (state?.productType) {
      navigate(`/${state.productType}`);
    } else {
      navigate('/');
    }
  };

  if (!state?.prompt) {
    return null;
  }

  return (
    <div className="bg-[#1a1a1a] min-h-screen py-12">
      <div className="container mx-auto px-4">
        <div className="mb-6">
          <button 
            onClick={handleBackToPrompt}
            className="flex items-center text-white hover:text-purple-400 transition-colors"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            返回提示詞編輯
          </button>
        </div>

        {/* Page Title */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-white mb-2">AI 設計生成</h1>
          <p className="text-gray-400">根據您的需求生成客製化產品設計</p>
        </div>

        {/* Prompts Container */}
        <div className="cm-dark-container p-8 mb-8 max-w-4xl mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            <div>
              <h2 className="text-xl font-semibold mb-4 text-white">原始提示詞</h2>
              <div className="bg-gray-800/50 p-4 rounded-md">
                <p className="text-gray-300">{state.prompt}</p>
              </div>
            </div>

            <div>
              <h2 className="text-xl font-semibold mb-4 text-white">AI 優化後的提示詞</h2>
              <div className="bg-purple-900/30 p-4 rounded-md border-l-4 border-purple-500">
                <p className="text-white">{refinedPrompt || '正在優化中...'}</p>
              </div>
            </div>
          </div>
        </div>

        {loading ? (
          <div className="text-center py-16">
            <div className="cm-gradient inline-flex items-center justify-center p-2 rounded-full mb-4">
              <div className="animate-spin rounded-full h-12 w-12 border-4 border-white border-opacity-30 border-t-white"></div>
            </div>
            <p className="text-white text-xl">正在生成設計...</p>
            <p className="text-gray-400 mt-2">使用 Cooler Master AI 生成創新設計方案</p>
          </div>
        ) : (
          <>
            <h2 className="text-2xl font-bold text-white mb-6 text-center">設計方案</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12 max-w-5xl mx-auto">
              {mockImages.map((imageUrl, index) => (
                <div
                  key={index}
                  className={`cm-product-card relative cursor-pointer overflow-hidden transition-all ${
                    selectedImage === imageUrl ? 'ring-2 ring-purple-500 transform scale-[1.02]' : ''
                  }`}
                  onClick={() => handleImageSelect(imageUrl)}
                >
                  <div className="relative">
                    <img
                      src={imageUrl}
                      alt={`設計方案 ${index + 1}`}
                      className="w-full h-52 object-cover"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/60 to-transparent"></div>
                    <div className="absolute bottom-0 left-0 right-0 p-3 text-white font-medium">
                      設計方案 {index + 1}
                    </div>
                    {selectedImage === imageUrl && (
                      <div className="absolute top-2 right-2 bg-purple-500 text-white p-1 rounded-full">
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                          <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                        </svg>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>

            {selectedImage && (
              <div className="max-w-3xl mx-auto">
                <div className="cm-dark-container p-8">
                  <h2 className="text-2xl font-semibold mb-6 text-white">設計評分與回饋</h2>

                  <div className="mb-6">
                    <h3 className="text-lg font-medium text-white mb-3">評分</h3>
                    <div className="flex space-x-3 mb-4">
                      {[1, 2, 3, 4, 5].map((rating) => (
                        <button
                          key={rating}
                          className="w-12 h-12 rounded-full flex items-center justify-center bg-gray-800 hover:bg-purple-900 text-white font-medium transition-colors"
                          onClick={() => console.log(`Rated: ${rating}`)}
                        >
                          {rating}
                        </button>
                      ))}
                    </div>
                  </div>

                  <div className="mb-6">
                    <h3 className="text-lg font-medium text-white mb-3">評論</h3>
                    <textarea
                      className="w-full px-4 py-3 border border-gray-700 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent bg-gray-800 text-white"
                      rows={3}
                      placeholder="請分享您對此設計的想法..."
                    />
                  </div>

                  <div className="flex space-x-4">
                    <button
                      onClick={handleSaveToKnowledgeBase}
                      className="flex-1 cm-button-primary py-3 px-4 rounded-md font-medium"
                    >
                      加入知識庫
                    </button>
                    <button
                      className="px-4 py-3 border border-purple-500 text-purple-400 rounded-md hover:bg-purple-900/30"
                    >
                      下載設計
                    </button>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default Generator;
