import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home: React.FC = () => {
  const navigate = useNavigate();

  const productTypes = [
    { 
      id: 'case', 
      name: '主機外殼',
      description: '創造獨特的電腦外觀設計'
    },
    { 
      id: 'cooler', 
      name: '散熱器',
      description: '打造高效能的散熱解決方案'
    },
    { 
      id: 'psu', 
      name: '電源供應器',
      description: '設計穩定可靠的電源系統'
    },
    { 
      id: 'furniture', 
      name: '遊戲家具',
      description: '開發舒適的遊戲空間配件'
    },
  ];

  const handleProductClick = (productId: string) => {
    navigate('/generator', { 
      state: { 
        productType: productId,
        prompt: '' // 初始提示詞為空
      } 
    });
  };

  return (
    <div className="home-container">
      <h1 className="header-text">Don't settle for ordinary.</h1>
      <h2 className="create-text">Create <span>anything</span>.</h2>

      <div className="products-grid">
        {productTypes.map((product) => (
          <div 
            key={product.id}
            className={`product-card ${product.id}`}
            onClick={() => handleProductClick(product.id)}
          >
            <div>
              <h2>{product.name}</h2>
              <p>{product.description}</p>
            </div>
            <button className="product-button">
              LAUNCH {product.id.toUpperCase()}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
