import React, { useState, useCallback, useEffect } from 'react';
import ReactFlow, {
  Node,
  Edge,
  Controls,
  Background,
  MiniMap,
  useNodesState,
  useEdgesState,
  addEdge,
  Connection,
  Panel,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { PlusIcon, ClockIcon, ArrowsPointingOutIcon, XMarkIcon } from '@heroicons/react/24/outline';
import { NodeEditor } from './NodeEditor';
import { Timeline } from './Timeline';

// 定義節點類型
export interface MindmapNode extends Node {
  data: {
    prompt: string;
    tags: string[];
    imageUrl: string;
    source: 'user' | 'ai';
    timestamp: number;
  };
}

// 定義 API 響應類型
interface ApiResponse {
  nodes: MindmapNode[];
  edges: Edge[];
}

// 初始節點
const initialNodes: MindmapNode[] = [
  {
    id: 'root',
    type: 'default',
    position: { x: 0, y: 0 },
    data: {
      prompt: 'Root',
      tags: ['root'],
      imageUrl: '',
      source: 'user',
      timestamp: Date.now(),
    },
  },
];

const initialEdges: Edge[] = [];

export const Mindmap: React.FC = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [selectedNode, setSelectedNode] = useState<MindmapNode | null>(null);
  const [showTimeline, setShowTimeline] = useState(false);

  // 從後端獲取節點數據
  const fetchNodes = useCallback(async () => {
    try {
      const response = await fetch('/api/nodes');
      const data: ApiResponse = await response.json();
      setNodes(data.nodes);
      setEdges(data.edges);
    } catch (error) {
      console.error('Error fetching nodes:', error);
    }
  }, [setNodes, setEdges]);

  // 新增節點
  const addNode = useCallback(async (nodeData: Omit<MindmapNode['data'], 'timestamp'>) => {
    try {
      const response = await fetch('/api/nodes', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...nodeData,
          timestamp: Date.now(),
        }),
      });
      const newNode: MindmapNode = await response.json();
      setNodes((nds) => [...nds, newNode]);
    } catch (error) {
      console.error('Error adding node:', error);
    }
  }, [setNodes]);

  // 自動延伸心智圖
  const autoExtendMindmap = useCallback(async () => {
    try {
      const response = await fetch('/api/extend', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          nodes: nodes.map(node => ({
            id: node.id,
            data: node.data,
          })),
        }),
      });
      const newNodes: MindmapNode[] = await response.json();
      setNodes((nds) => [...nds, ...newNodes]);
    } catch (error) {
      console.error('Error extending mindmap:', error);
    }
  }, [nodes, setNodes]);

  // 處理節點點擊
  const onNodeClick = useCallback((event: React.MouseEvent, node: Node) => {
    setSelectedNode(node as MindmapNode);
  }, []);

  // 處理邊的連接
  const onConnect = useCallback(
    (params: Connection) => setEdges((eds) => addEdge(params, eds)),
    [setEdges],
  );

  // 定時自動延伸
  useEffect(() => {
    const interval = setInterval(() => {
      autoExtendMindmap();
    }, 24 * 60 * 60 * 1000); // 每24小時執行一次

    return () => clearInterval(interval);
  }, [autoExtendMindmap]);

  // 初始載入數據
  useEffect(() => {
    fetchNodes();
  }, [fetchNodes]);

  return (
    <div className="w-full h-screen bg-gray-900">
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onConnect={onConnect}
        onNodeClick={onNodeClick}
        fitView
        fitViewOptions={{ padding: 0.2 }}
        defaultViewport={{ x: 0, y: 0, zoom: 1 }}
        style={{ width: '100%', height: '100%' }}
        className="bg-gray-900"
      >
        <Background color="#6b7280" gap={16} size={1} />
        <Controls className="bg-gray-800 border-gray-700" />
        <MiniMap 
          style={{ 
            backgroundColor: '#1f2937'
          }} 
          className="!bottom-24 !right-2"
        />
        <Panel position="top-right" className="flex gap-2">
          <button
            onClick={() => addNode({
              prompt: 'New Node',
              tags: [],
              imageUrl: '',
              source: 'user',
            })}
            className="p-2 bg-purple-600 rounded-lg hover:bg-purple-700 transition-colors"
          >
            <PlusIcon className="w-5 h-5 text-white" />
          </button>
          <button
            onClick={() => setShowTimeline(!showTimeline)}
            className={`p-2 rounded-lg transition-colors ${
              showTimeline ? 'bg-purple-700' : 'bg-purple-600 hover:bg-purple-700'
            }`}
          >
            <ClockIcon className="w-5 h-5 text-white" />
          </button>
        </Panel>

        {showTimeline && (
          <Panel position="bottom-center" className="w-full max-w-4xl">
            <div className="bg-gray-800 rounded-lg shadow-lg">
              <div className="flex justify-between items-center p-4 border-b border-gray-700">
                <h3 className="text-lg font-semibold text-white">Timeline</h3>
                <button
                  onClick={() => setShowTimeline(false)}
                  className="p-1 hover:bg-gray-700 rounded-full transition-colors"
                >
                  <XMarkIcon className="w-5 h-5 text-white" />
                </button>
              </div>
              <div className="h-64 overflow-y-auto p-4">
                <Timeline
                  nodes={nodes}
                  onSelectNode={(node: MindmapNode) => {
                    setSelectedNode(node);
                    setShowTimeline(false);
                  }}
                />
              </div>
            </div>
          </Panel>
        )}
      </ReactFlow>

      {/* 節點編輯面板 */}
      {selectedNode && (
        <div className="fixed right-0 top-0 w-80 bg-gray-800 p-4 rounded-lg shadow-lg">
          <NodeEditor
            node={selectedNode}
            onUpdate={(updatedNode) => {
              setNodes((nds) =>
                nds.map((node) =>
                  node.id === updatedNode.id ? updatedNode : node
                )
              );
            }}
            onClose={() => setSelectedNode(null)}
          />
        </div>
      )}
    </div>
  );
};
