import React from 'react';
import { useNavigate } from 'react-router-dom';

const placeholderImages = Array.from({ length: 9 }, (_, i) => ({
  url: `https://via.placeholder.com/120x90?text=Image+${i + 1}`,
  name: `Image ${i + 1}`
}));

const SelectImagePage: React.FC = () => {
  const navigate = useNavigate();

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-white">
      <h1 className="text-2xl font-bold mb-6 text-black">選擇 Base Image</h1>
      <div className="grid grid-cols-3 gap-4 mb-8">
        {placeholderImages.map((img, idx) => (
          <div key={idx} className="flex flex-col items-center bg-purple-50 border border-purple-200 rounded-lg p-2 shadow hover:shadow-lg transition">
            <img src={img.url} alt={img.name} className="w-28 h-20 object-cover rounded mb-2" />
            <span className="text-xs text-gray-700">{img.name}</span>
          </div>
        ))}
      </div>
      <button
        onClick={() => navigate(-1)}
        className="px-6 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700"
      >
        返回
      </button>
    </div>
  );
};

export default SelectImagePage;
