import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const CasePage: React.FC = () => {
  const navigate = useNavigate();
  const [prompt, setPrompt] = useState<string>('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (prompt) {
      navigate('/generator', { state: { productType: 'case', prompt } });
    }
  };

  const handleBackToSelection = () => {
    navigate('/');
  };

  return (
    <div className="bg-[#1a1a1a] min-h-screen py-12">
      <div className="container mx-auto px-4">
        <div className="mb-6">
          <button 
            onClick={handleBackToSelection}
            className="flex items-center text-white hover:text-purple-400 transition-colors"
          >
            <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-1" viewBox="0 0 20 20" fill="currentColor">
              <path fillRule="evenodd" d="M9.707 16.707a1 1 0 01-1.414 0l-6-6a1 1 0 010-1.414l6-6a1 1 0 011.414 1.414L5.414 9H17a1 1 0 110 2H5.414l4.293 4.293a1 1 0 010 1.414z" clipRule="evenodd" />
            </svg>
            返回選擇
          </button>
        </div>

        {/* Page Title */}
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold text-white mb-2">主機外殼設計</h1>
          <p className="text-gray-400">使用 AI 輔助設計您的下一個主機外殼</p>
        </div>

        {/* Design Form */}
        <div className="max-w-2xl mx-auto">
          <div className="cm-dark-container p-8 mb-8">
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label htmlFor="prompt" className="block text-sm font-medium text-white mb-2">
                  設計描述
                </label>
                <textarea
                  id="prompt"
                  value={prompt}
                  onChange={(e) => setPrompt(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-700 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent bg-gray-800 text-white"
                  rows={6}
                  placeholder="請描述您想要的主機外殼設計風格、特點、功能需求..."
                  required
                />
              </div>

              <div className="pt-4">
                <button
                  type="submit"
                  className="w-full cm-button-primary py-3 px-6 rounded-md text-lg font-medium hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:ring-offset-2 focus:ring-offset-gray-900"
                >
                  開始生成設計
                </button>
              </div>
            </form>
          </div>

          {/* Design Tips */}
          <div className="bg-gradient-to-r from-purple-900/40 to-indigo-900/40 rounded-lg p-6">
            <h3 className="text-white text-lg font-medium mb-3">提示指南</h3>
            <ul className="text-gray-300 space-y-2 list-disc pl-5">
              <li>詳細描述您希望的外觀風格（現代、復古、未來等）</li>
              <li>指定尺寸規格和兼容性需求（ATX、ITX等）</li>
              <li>描述特殊功能（RGB燈效、風扇配置、散熱需求等）</li>
              <li>提及顏色偏好和材質要求</li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CasePage;
